import Container from "react-bootstrap/Container";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Footer from "../components/Footer";
import HeaderIT from "../components/HeaderIT";
import { useState } from 'react';

function ContattiIT() {

    const [language, setLanguage] = useState(true);

    const italianChoice = () => {
        const newValue = true;
        setLanguage(newValue);
    }

    const engChoice = () => {
        const newValue = false;
        setLanguage(newValue);
    }

    return (
        <>
            <HeaderIT />
            <section id='contatti'>
                <Container>
                    <h2>{language ? 'Contatti' : 'Contacts'}</h2>
                    <p>Il Ristorante Galleria è aperto <span className="bold">tutti i giorni dell'anno</span> (ad eccezione del 25 Dicembre), <span className="bold">dalle 10.00 alle 00.30</span>.
                        Ristorante Galleria è sempre aperto dopo gli spettacoli serali del Teatro alla Scala.</p>
                    <Row>
                        <Col lg={6} md={12}>
                            <iframe id='map' src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2798.185790219822!2d9.187730615476418!3d45.46606117910096!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x4786c6ae786cc08d%3A0xe5480b103c7b561e!2sGalleria%20Restaurant!5e0!3m2!1sen!2sit!4v1668685129778!5m2!1sen!2sit" width="100%" height="350" style={{ border: 0 }} allowFullScreen="" loading="lazy" referrerPolicy="no-referrer-when-downgrade"></iframe>
                        </Col>
                        <Col lg={6} md={12} className='d-flex flex-column justify-content-evenly'>
                            <p>Per prenotazioni o informazioni, si prega di chiamare il numero <a href='tel:+390286464912'>+39 02 86464912</a> o inviare una email all'indirizzo <a href='mailto:info@ristorantegalleria.it'>info@ristorantegalleria.it</a>.
                                Saremo lieti di rispondere a qualsiasi vostra richiesta.</p>
                            <p>
                                Galleria Vittorio Emanuele II, 75 - 20121
                            </p>
                        </Col>
                    </Row>
                </Container>
            </section>
            <Footer />
        </>
    );
}

export default ContattiIT;
