import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';

function HeaderIT(props) {

    let language = props.isItalian;



    return (
        <>
            <header>
                <Navbar expand='lg'>
                    <Container className='justify-content-end'>
                        <a className="navbar-brand" href="/">
                            <img alt='logo del ristorante Galleria' src={`${require('../images/ristorantegalleria_logo.png')}`} />
                        </a>
                        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <Navbar.Collapse id="basic-navbar-nav" className='justify-content-end'>
                            <Nav>
                                <Nav.Link href='/storia'>{language ? (
                                    'STORIA'
                                ) : (
                                    'HISTORY'
                                )
                                }</Nav.Link>
                                <Nav.Link href='/ristorante'>RISTORANTE</Nav.Link>
                                <Nav.Link href='/pizza'>PIZZA</Nav.Link>
                                <Nav.Link href='/pasticceria'>PASTICCERIA</Nav.Link>
                                <Nav.Link href='/contatti'>CONTATTI</Nav.Link>
                                <Nav.Link href='/news'>NEWS</Nav.Link>
                                <Nav.Link onClick={props.isItalian = true}>ITALIANO</Nav.Link>
                                <Nav.Link onClick={props.isItalian = false}>ENGLISH</Nav.Link>
                            </Nav>
                        </Navbar.Collapse>
                    </Container>
                </Navbar>
            </header>
            <div className='images-header'>
                <Container>
                    <img className='logo' alt='logo di Galleria CafÃ¨ Restaurant' src={`${require('../images/ristorantegalleria_logo.png')}`} />
                </Container>
            </div>
        </>
    );
}

export default HeaderIT;
